pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import {
    ISuperfluid,
    ISuperfluidToken
} from "../interfaces/superfluid/ISuperfluid.sol";

import {
    IConstantFlowAgreementV1
} from "../interfaces/agreements/IConstantFlowAgreementV1.sol";

library CFAv1Library {

    struct InitData {
        ISuperfluid host;
        IConstantFlowAgreementV1 cfa;
    }

    //@dev for working with the constant flow agreement within solidity
    //the first set of functions are each for callAgreement()
    //the second set of functions are each for use in callAgreementWithContext()

     //create flow without userData
    function createFlow(
        InitData storage cfaLibrary,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.createFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x" //empty user data
        );
    }


    //create flow with userData
    function createFlow(
        InitData storage cfaLibrary, 
        address receiver,
        ISuperfluidToken token,
        int96 flowRate,
        bytes memory userData
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.createFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            userData
        );
    }

    //update flow without userData
    function updateFlow(
        InitData storage cfaLibrary,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.updateFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x" //empty user data
        );
    }


    //update flow with userData
    function updateFlow(
        InitData storage cfaLibrary,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate,
        bytes memory userData
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.updateFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            userData
        );
    }

    //delete flow
    function deleteFlow(
        InitData storage cfaLibrary,
        address sender,
        address receiver,
        ISuperfluidToken token
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.deleteFlow.selector,
                token,
                sender,
                receiver,
                new bytes(0) // placeholder
            ),
            "0x" //empty user data
        );
    }


    //delete flow with userData 
    function deleteFlow(
        InitData storage cfaLibrary,
        address sender,
        address receiver,
        ISuperfluidToken token,
        bytes memory userData
    ) internal {
        cfaLibrary.host.callAgreement(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.deleteFlow.selector,
                token,
                sender,
                receiver,
                new bytes(0) // placeholder
            ),
            userData
        );
    }

  //create flow with ctx 
    function createFlowWithCtx(
        InitData storage cfaLibrary,
        bytes memory ctx,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate
    ) internal returns (bytes memory newCtx) {
        (newCtx, ) = cfaLibrary.host.callAgreementWithContext(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.createFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x", //empty user data
            ctx
        );
    }

    //create flow with ctx and userData
    function createFlowWithCtx(
        InitData storage cfaLibrary,
        bytes memory ctx,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate,
        bytes memory userData
    ) internal returns (bytes memory newCtx) {
        (newCtx, ) = cfaLibrary.host.callAgreementWithContext(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.createFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            userData,
            ctx
        );
    }

  //update flow with ctx but without userData 
    function updateFlowWithCtx(
        InitData storage cfaLibrary,
        bytes memory ctx,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate
    ) internal returns (bytes memory newCtx) {
        (newCtx, ) = cfaLibrary.host.callAgreementWithContext(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.updateFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x", //empty user data
            ctx
        );
    }

    //update flow with ctx and userData
    function updateFlowWithCtx(
        InitData storage cfaLibrary,
        bytes memory ctx,
        address receiver,
        ISuperfluidToken token,
        int96 flowRate,
        bytes memory userData
    ) internal returns (bytes memory newCtx) {
        (newCtx, ) = cfaLibrary.host.callAgreementWithContext(
            cfaLibrary.cfa,
            abi.encodeWithSelector(
                cfaLibrary.cfa.updateFlow.selector,
                token,
                receiver,
                flowRate,
                new bytes(0) // placeholder
            ),
            userData,
            ctx
